sys_script_fix.script:
var gr = new GlideRecord('sys_update_xml')
gr.addEncodedQuery('sys_updated_onONToday@javascript:gs.beginningOfToday()@javascript:gs.endOfToday()^ORsys_updated_onONYesterday@javascript:gs.beginningOfYesterday()@javascript:gs.endOfYesterday()');
gr.query();
while(gr.next()){
	gr.setValue('application','Incident Management');
	gr.update();
}run();
